mapper.select:
SELECT a.id, a.documnet_No, a.asepsis_Code, a.asepsis_Name, a.asepsis_Spec, a.units, a.belong_Dept, a.anti_Date, a.anti_Operator, a.anti_Ways, a.ster_Operator, a.ster_Date, a.pack_Operator, a.pack_Date, a.memos, a.asepsis_State, a.amount, a.amount as amountAnti, a.anti_Batch_No, a.imp_Date, a.boiler_No, a.boiler_Times, a.checker, a.item_No, a.pack_Ways, a.clean_Ways, a.clean_No, a.clean_Times, a.org_Id FROM asepsis_anti_rec a
a.asepsis_state = #{asepsisState}
and a.org_id = #{orgId}
AND a.belong_dept = #{belongDept}
AND a.asepsis_name = #{asepsisName}

SELECT

FROM asepsis_anti_rec a

WHERE a.id = #{id}

mapper.update:
UPDATE asepsis_anti_rec
SET asepsis_state =  #{asepsisState}



    , ster_operator = #{sterOperator}


    , checker = #{checker}


    , clean_ways = #{cleanWays}


    , clean_no = #{cleanNo}


    , clean_times = #{cleanTimes}


    , STER_DATE = #{sterDate}




    , pack_operator = #{packOperator}


    , pack_ways = #{packWays}


    , pack_DATE = #{packDate}




    , anti_operator = #{antiOperator}


    , anti_ways = #{antiWays}


    , boiler_no = #{boilerNo}


    , boiler_times = #{boilerTimes}


    , anti_DATE = #{antiDate}


    , amount = ${amountAnti}

WHERE id=${id} and documnet_no = #{documnetNo}


        and asepsis_state =  '0'


        and asepsis_state =  '1'


        and asepsis_state =  '2'



    and amount = ${amount}

UPDATE asepsis_anti_rec SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
