mapper.select:
SELECT

FROM ASEPSIS_DICT a
WHERE a.id = #{id}

select a.id, a.asepsis_code, a.item_class, a.asepsis_name, a.asepsis_spec, a.units, a.anti_price, a.noback_price, a.noback_price, a.belong_dept, a.memos, a.input_code, a.valid_days, a.clean_price, a.pack_price, a.asep_price, a.item_rollback_flag, a.flag, a.org_id, b.dept_name, o.label from asepsis_dict a, dept_dict b,org_sys_dict o where a.org_id =#{orgId} and a.belong_dept = b.dept_code and o.value=a.units
and a.belong_dept=#{belongDept}
and a.asepsis_name=#{asepsisName}

SELECT DISTINCT
FROM ASEPSIS_DICT a, ASEPSIS_STOCK s
a.asepsis_code = s.item_code and flag = '1' and s.from_dept = a.belong_dept
and a.belong_dept = #{belongDept}
and a.org_id = #{orgId}
and (s.anti_date+a.valid_days) >= sysdate and s.amount > 0

SELECT

FROM ASEPSIS_DICT a


    a.org_id = #{orgId}

        and a.BELONG_DEPT=#{belongDept}



        and a.ASEPSIS_NAME=#{asepsisName}

mapper.update:
UPDATE  ASEPSIS_DICT SET
    ITEM_CLASS = #{itemClass}
    ,ASEPSIS_CODE = #{asepsisCode}
    ,ASEPSIS_NAME = #{asepsisName}
    ,ASEPSIS_SPEC = #{asepsisSpec}
    ,UNITS = #{units}
    ,ANTI_PRICE = #{antiPrice}
    ,NOBACK_PRICE = #{nobackPrice}
    ,BELONG_DEPT = #{belongDept}
    ,MEMOS = #{memos}
    ,INPUT_CODE = #{inputCode}
    ,VALID_DAYS = #{validDays}
    ,CLEAN_PRICE = #{cleanPrice}
    ,PACK_PRICE = #{packPrice}
    ,ASEP_PRICE = #{asepPrice}
    ,ITEM_ROLLBACK_FLAG = #{itemRollbackFlag}
    ,FLAG = #{flag}
    ,ORG_ID = #{orgId}
WHERE id = #{id}
