mapper.select:
SELECT

FROM ASEPSIS_LEND_REC a
WHERE a.id = #{id}

SELECT

FROM ASEPSIS_LEND_REC a

    a.org_id =  #{orgId}

        and to_char(a.lend_date,'yyyy-MM-dd') = to_char(#{lendDate},'yyyy-MM-dd')


        and a.lend_date >= #{lendDateStart}


        and #{lendDateEnd} >= a.lend_date


        and a.to_dept = #{toDept}


        and a.lender = #{lender}


        and a.return_flag = #{returnFlag}

mapper.update:
UPDATE ASEPSIS_LEND_REC SET
    DOCUMENT_NO = #{documentNo}
    ,ITEM_NO = #{itemNo}
    ,TO_DEPT = #{toDept}
    ,LEND_DATE = #{lendDate}
    ,ITEM_CODE = #{itemCode}
    ,ITEM_NAME = #{itemName}
    ,ITEM_SPEC = #{itemSpec}
    ,LEND_AMOUNT = #{lendAmount}
    ,UNITS = #{units}
    ,RETURN_AMOUNT = #{returnAmount}
    ,RETURN_DATE = #{returnDate}
    ,RETURN_FLAG = #{returnFlag}
    ,OPERATOR = #{operator}
    ,LENDER = #{lender}
    ,MEMOS = #{memos}
    ,ANTI_FEE = #{antiFee}
    ,ANTI_FEE_SUM = #{antiFeeSum}
    ,ANTI_DATE = #{antiDate}
    ,EXP_DOCUMENT_NO = #{expDocumentNo}
    ,NOBACK_FEE = #{nobackFee}
    ,REQ_DATE = #{reqDate}
    ,REQ_OPERATOR = #{reqOperator}
    ,OPERATOR2 = #{operator2}
    ,ORG_ID = #{orgId}
    ,RETURN_MAN = #{returnMan}
WHERE id = #{id}
