mapper.select:
SELECT

FROM ASEPSIS_SEND_REC a
WHERE a.id = #{id}

mapper.update:
UPDATE INTO ASEPSIS_SEND_REC SET
    ID = #{id}
    ,DOCUMENT_NO = #{documentNo}
    ,ITEM_NO = #{itemNo}
    ,FROM_DEPT = #{fromDept}
    ,SEND_DATE = #{sendDate}
    ,ITEM_CODE = #{itemCode}
    ,ITEM_NAME = #{itemName}
    ,ITEM_SPEC = #{itemSpec}
    ,SEND_AMOUNT = #{sendAmount}
    ,GET_AMOUNT = #{getAmount}
    ,GET_DATE = #{getDate}
    ,GET_FLAG = #{getFlag}
    ,UNITS = #{units}
    ,OPERATOR = #{operator}
    ,SENDER = #{sender}
    ,MEMOS = #{memos}
    ,ANTI_FEE = #{antiFee}
    ,ANTI_FEE_SUM = #{antiFeeSum}
    ,NOBACK_FEE = #{nobackFee}
    ,REQ_DATE = #{reqDate}
    ,REQ_OPERATOR = #{reqOperator}
    ,ORG_ID = #{orgId}
WHERE id = #{id}
