mapper.select:
SELECT

FROM ASEPSIS_SEND_REC a
WHERE a.id = #{id}

SELECT

FROM ASEPSIS_SEND_REC a

    a.org_id =  #{orgId}
    and s.org_id =  #{orgId}

SELECT ,nvl(s.amount,0) stock,d.dept_name fromDeptName FROM ASEPSIS_SEND_REC a,(select sum(s.amount) amount,s.document_no from asepsis_stock s,asepsis_dict d where s.item_code = d.asepsis_code and s.org_id=#{orgId} and (s.anti_date+d.valid_days) > sysdate group by s.document_no) s, (select distinct dept_code,dept_name from dept_dict where org_id = #{orgId}) d
a.org_id = #{orgId} and a.from_dept = d.dept_code and a.document_no = s.document_no(+)
and a.send_date >= #{sendDateStart}
and #{sendDateEnd} >= a.send_date
and a.from_dept = #{fromDept}
and a.sender = #{sender}
order by a.from_dept,a.document_no

select max(document_no)
from asepsis_send_rec t
where document_no like 'S'||to_char(sysdate,'yyMMdd')||'%' and org_id = #{1}

mapper.update:
UPDATE ASEPSIS_SEND_REC SET
    DOCUMENT_NO = #{documentNo}
    ,ITEM_NO = #{itemNo}
    ,FROM_DEPT = #{fromDept}
    ,SEND_DATE = #{sendDate}
    ,ITEM_CODE = #{itemCode}
    ,ITEM_NAME = #{itemName}
    ,ITEM_SPEC = #{itemSpec}
    ,SEND_AMOUNT = #{sendAmount}
    ,GET_AMOUNT = #{getAmount}
    ,GET_DATE = #{getDate}
    ,GET_FLAG = #{getFlag}
    ,UNITS = #{units}
    ,OPERATOR = #{operator}
    ,SENDER = #{sender}
    ,MEMOS = #{memos}
    ,ANTI_FEE = #{antiFee}
    ,ANTI_FEE_SUM = #{antiFeeSum}
    ,NOBACK_FEE = #{nobackFee}
    ,REQ_DATE = #{reqDate}
    ,REQ_OPERATOR = #{reqOperator}
    ,ORG_ID = #{orgId}
WHERE id = #{id}
