mapper.select:
SELECT

FROM ASEPSIS_SEND_REC a
WHERE a.id = #{id}

SELECT
,a.send_Amount-a.get_Amount as stock
FROM ASEPSIS_SEND_REC a,asepsis_stock s

    a.org_id =  #{orgId}
    and s.org_id =  #{orgId}
    and a.document_no = s.document_no

        and to_char(a.send_date,'yyyy-MM-dd') = to_char(#{sendDate},'yyyy-MM-dd')


        and a.send_date >= #{sendDateStart}


        and #{sendDateEnd} >= a.send_date


        and a.from_dept = #{fromDept}


        and a.sender = #{sender}


        and a.get_flag = #{getFlag}



        and a.document_No = #{documentNo}


        and a.item_Code = #{itemCode}


        and a.item_No = #{itemNo}

mapper.update:
UPDATE ASEPSIS_SEND_REC SET
    DOCUMENT_NO = #{documentNo}
    ,ITEM_NO = #{itemNo}
    ,FROM_DEPT = #{fromDept}
    ,SEND_DATE = #{sendDate}
    ,ITEM_CODE = #{itemCode}
    ,ITEM_NAME = #{itemName}
    ,ITEM_SPEC = #{itemSpec}
    ,SEND_AMOUNT = #{sendAmount}
    ,GET_AMOUNT = #{getAmount}
    ,GET_DATE = #{getDate}
    ,GET_FLAG = #{getFlag}
    ,UNITS = #{units}
    ,OPERATOR = #{operator}
    ,SENDER = #{sender}
    ,MEMOS = #{memos}
    ,ANTI_FEE = #{antiFee}
    ,ANTI_FEE_SUM = #{antiFeeSum}
    ,NOBACK_FEE = #{nobackFee}
    ,REQ_DATE = #{reqDate}
    ,REQ_OPERATOR = #{reqOperator}
    ,ORG_ID = #{orgId}
WHERE id = #{id}
