mapper.select:
SELECT

FROM ASEPSIS_STOCK a
WHERE a.id = #{id}

SELECT a.ID, a.DOCUMENT_NO, a.from_dept, a.ITEM_CODE, a.ITEM_NAME, a.ITEM_SPEC, a.AMOUNT, a.UNITS, a.MEMOS, a.ANTI_DATE, a.OPERATOR, a.ALTER_DATE, a.ANTI_BATCH_NO, a.ITEM_NO, a.org_id, b.dept_name as fromDeptName, o.label FROM ASEPSIS_STOCK a,DEPT_DICT b,ORG_SYS_DICT o a.org_id = #{orgId}
and a.FROM_DEPT = #{fromDept}
and a.item_code = #{itemCode}
and a.item_name = #{itemName}
and a.anti_date >= #{antiDateStart}
and #{antiDateEnd} >= a.anti_date
and a.FROM_DEPT = b.dept_code and o.value=a.units
and a.document_no = #{documentNo}
order by a.document_no, a.anti_date

SELECT

FROM ASEPSIS_STOCK a
 a.org_id =  #{orgId}

        and a.FROM_DEPT = #{fromDept}


        and a.item_code = #{itemCode}


        and a.document_no like #{documentNo} || '%'


order by a.document_no, a.anti_date

SELECT

FROM ASEPSIS_STOCK a,ASEPSIS_DICT d
 a.org_id =  #{orgId}
    and d.asepsis_code = a.item_code and d.flag = '1'
    and a.from_dept = d.belong_dept
    and (a.anti_date+d.valid_days) >= sysdate
    and a.amount > 0

        and a.FROM_DEPT = #{fromDept}


        and a.item_code = #{itemCode}


order by a.document_no, a.anti_date

mapper.update:
UPDATE  ASEPSIS_STOCK SET
    DOCUMENT_NO = #{documentNo}
    ,FROM_DEPT = #{fromDept}
    ,ITEM_CODE = #{itemCode}
    ,ITEM_NAME = #{itemName}
    ,ITEM_SPEC = #{itemSpec}
    ,AMOUNT = #{amount}
    ,UNITS = #{units}
    ,MEMOS = #{memos}
    ,ANTI_DATE = #{antiDate}
    ,OPERATOR = #{operator}
    ,ALTER_DATE = #{alterDate}
    ,ANTI_BATCH_NO = #{antiBatchNo}
    ,ITEM_NO = #{itemNo}
    ,ORG_ID = #{orgId}
WHERE id = #{id}
