mapper.select:
SELECT

FROM clinic_item_dict a

WHERE a.id = #{id}

SELECT ,d.dept_name expand3Name, p.freq_desc expand4Name,l.class_name expand2Name,s.label expand1Name FROM clinic_item_dict a,dept_dict d,perform_freq_dict p,lab_item_class_dict l ,(select * from sys_dict where type = 'SPECIMAN_DICT' and del_flag = '0') s
a.del_flag = #{DEL_FLAG_NORMAL} AND a.item_class = #{itemClass}
AND a.item_code LIKE #{itemCode} || '%'
AND a.input_code LIKE #{inputCode} || '%'
AND a.org_id = #{orgId}
AND a.expand3 = d.id(+) AND a.expand4 = p.id(+) AND a.expand2 = l.id(+) AND a.expand1 = s.value(+)
ORDER BY ${page.orderBy}
ORDER BY a.item_code, a.update_date DESC

SELECT

FROM clinic_item_dict a



         a.item_code = #{itemCode}


        AND a.item_name = #{itemName}


        AND a.org_id = #{orgId}

SELECT *
FROM clinic_item_dict a
WHERE a.org_id = #{0}
AND a.item_name = #{1}
AND a.item_code = #{2}

SELECT

FROM clinic_item_dict a


    a.del_flag = #{DEL_FLAG_NORMAL}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC
