mapper.select:
SELECT 

FROM course_record_eachdis a

WHERE a.id = #{id}

SELECT 

FROM course_record_eachdis a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM course_record_eachdis a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE course_record_eachdis SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
