mapper.select:
SELECT 

FROM doct_drug_presc_master a

WHERE a.id = #{id}

SELECT
FROM doct_drug_presc_master a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM doct_drug_presc_master a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE doct_drug_presc_master SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
