mapper.select:
SELECT 

FROM electron_leave_hospital a

WHERE a.id = #{id}

SELECT 

FROM electron_leave_hospital a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM electron_leave_hospital a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE electron_leave_hospital SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

