mapper.sql:
a.id AS "id", a.parent_id AS "parentId", a.parent_ids as "parentIds", a.type as "type", a.description as "description", a.basis as "basis", a.diagnosis_id AS "diagnosisId", a.ITEM_NO AS "itemNo", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.remarks AS "remarks",
a.del_flag AS "delFlag", a.patient_id AS "patientId", a.TREAT_DAYS AS "treatDays", a.TREAT_RESULT AS "treatResult", a.OPER_TREAT_INDICATOR AS "operTreatIndicator", a.PATHOLOGY_NO AS "pathologyNo", a.DIAGNOSIS_DOC AS "diagnosisDoc", a.INOROUT_FLAG AS "inOrOutFlag", a.diagnosis_date AS "diagnosisDate", a.diagnosis_parent AS "diagnosisParent"
/*"diagnosis_type" as "diagnosis_type"*/

mapper.select:
SELECT 

FROM emr_diagnosis a

WHERE a.id = #{id}

		SELECT 

		FROM emr_diagnosis a


			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.parent_id=#{parentId}


                AND a.parent_id in (

                    #{item.id}

                )


                AND a.parent_ids=#{parentIds}





				ORDER BY ${page.orderBy}


				ORDER BY  a.type  ASC,a.create_date ASC,a.item_no ASC

SELECT 

FROM emr_diagnosis a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
d.LABEL || ':' ||
(CASE
WHEN i.ZHONGWEN_MINGCHENG is null
THEN a.diagnosis_id
ELSE i.ZHONGWEN_MINGCHENG
END
) AS icdAndTypeNmae,

FROM emr_diagnosis a
left join sys_dict d on d.type='diagnosis_type' and d.value=a.type
left join emr_data_icd10 i on a.diagnosis_id = i.code

	a.del_flag = #{DEL_FLAG_NORMAL}
	and a.INOROUT_FLAG = #{inOrOutFlag}

	and a.clinic_id = #{clinicId}


	and a.visit_id = #{visitId}

mapper.insert:
INSERT INTO emr_diagnosis( id, parent_id, diagnosis_id, item_no, type, create_by, create_date, update_by, update_date, remarks, del_flag, description, basis,
patient_id, TREAT_DAYS, TREAT_RESULT, OPER_TREAT_INDICATOR, PATHOLOGY_NO, DIAGNOSIS_DOC, INOROUT_FLAG, diagnosis_date, diagnosis_parent, parent_ids
) VALUES ( #{id}, #{parentId}, #{diagnosisId}, #{itemNo}, #{type}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{remarks}, #{delFlag}, #{description}, #{basis}, #{patientId}, #{treatDays}, #{treatResult}, #{operTreatIndicator}, #{pathologyNo}, #{diagnosisDoc}, #{inOrOutFlag}, #{diagnosisDate}, #{diagnosisParent}, #{parentIds}
)

mapper.update:
UPDATE emr_diagnosis SET 
	del_flag = '1'
WHERE id = #{id}
