mapper.select:
SELECT 

FROM outp_orders_costs a

WHERE a.id = #{id}

SELECT 

FROM outp_orders_costs a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM outp_orders_costs a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE outp_orders_costs SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
