mapper.select:
SELECT 

FROM outp_treat_rec a

WHERE a.id = #{id}

SELECT 

FROM outp_treat_rec a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM outp_treat_rec a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
