mapper.select:
SELECT 

FROM scheduled_operation_name a

WHERE a.id = #{id}

		SELECT 

		FROM scheduled_operation_name a


			a.del_flag = '0'

              and   a.schedule_id = #{scheduleId}



               and  a.clinic_id = #{clinicId}


				ORDER BY a.update_date DESC

SELECT 

FROM scheduled_operation_name a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE scheduled_operation_name SET 
	del_flag = '1'
WHERE id = #{id}
