mapper.select:
SELECT DISTINCT VISIT_DATE AS "visitDate", VISIT_NO AS "visitNo", PRESC_NO AS "prescNo", ITEM_CLASS AS "itemClass", CHARGE_INDICATOR AS "chargeIndicator", PRESC_ATTR AS "prescAttr" FROM OUTP_PRESC
del_flag='0'
and clinic_id=#{clinicId}
and org_id=#{orgId}
ORDER BY PRESC_NO DESC

select MAX(order_no) FROM outp_presc a

	a.del_flag='0'

	 and a.clinic_id=#{clinicId}


		and a.org_id=#{orgId}

