mapper.select:
SELECT 

FROM outp_payments_money a

WHERE a.id = #{id}

SELECT 

FROM outp_payments_money a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM outp_payments_money a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE outp_payments_money SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
