mapper.select:
SELECT 

FROM outp_rcpt_master a

WHERE a.id = #{id}

SELECT
FROM outp_rcpt_master a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM outp_rcpt_master a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE outp_rcpt_master SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT SUM(DECODE(TOTAL_CHARGES, 0, 1, 0)) as "freeCount", SUM(DECODE(CHARGE_INDICATOR, 2, DECODE(SIGN(TOTAL_CHARGES), -1, 1, 0), 0)) as "refundCount",
SUM(TOTAL_COSTS) as "sumCosts", SUM(TOTAL_CHARGES) as "sumCharges", SUM(DECODE(SIGN(TOTAL_CHARGES), -1, ABS(TOTAL_CHARGES), 0)) as "refundMoney", MIN(RCPT_NO) as "minRcptNo", MAX(RCPT_NO) as "maxRcptNo", COUNT(*) as "countNo" FROM OUTP_RCPT_MASTER WHERE DEL_FlAG = '0'
AND OPERATOR_NO = #{operatorNo}
/*如果交费标志是2（0-正常交费1-欠费 2-已退费）*/ AND CHARGE_INDICATOR <> 1 AND ACCT_NO IS NULL
AND VISIT_DATE <= to_date(to_char(#{visitDate},'yyyy-mm-dd hh24:mi:ss'), 'yyyy-mm-dd hh24:mi:ss')

mapper.update:
UPDATE outp_rcpt_master SET
	acct_no = #{acctNo}
WHERE  OUTP_RCPT_MASTER.ACCT_NO is null

	AND OUTP_RCPT_MASTER.OPERATOR_NO = #{operatorNo}


		AND OUTP_RCPT_MASTER.VISIT_DATE <=
   to_date(to_char(#{visitDate},'yyyy-mm-dd hh24:mi:ss'), 'yyyy-mm-dd hh24:mi:ss')
