mapper.select:
SELECT

FROM drug_inventory_check a

WHERE a.id = #{id}

SELECT 

FROM drug_inventory_check a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM drug_inventory_check a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE drug_inventory_check SET 
	del_flag = '1'
WHERE id = #{id}
