mapper.update:
UPDATE drug_stock SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT ,d.drug_name drugName,d.input_code inputCode FROM drug_stock a,(select distinct drug_code,drug_name,input_code from drug_dict where del_flag='0') d
a.del_flag = #{DEL_FLAG_NORMAL} and a.drug_code = d.drug_code
and a.supply_indicator = #{supplyIndicator}
and a.org_id = #{orgId}
and a.storage = #{storage}
and a.sub_storage = #{subStorage}
and (a.drug_code like #{q}||'%' or d.drug_name like #{q}||'%' or upper(d.input_code) like upper(#{q})||'%')
and a.quantity > 0 AND EXISTS (SELECT 1 FROM drug_price_list t WHERE d.drug_code = t.drug_code AND t.del_flag = #{delFlag} AND t.org_id = #{orgId} and a.drug_code = t.drug_code and a.drug_spec = t.drug_spec and a.firm_id = t.firm_id and a.org_id = t.org_id and a.units = t.units and sysdate>=t.start_date and(t.stop_date is null or (t.stop_date is not null and t.stop_date>sysdate)))
ORDER BY a.drug_code DESC
