mapper.select:
SELECT 

FROM drug_storage_dept a

WHERE a.id = #{id}

		SELECT 

		FROM drug_storage_dept a


			a.del_flag = #{DEL_FLAG_NORMAL}

                and a.org_id = #{orgId}


                and a.storage_type = #{storageType}




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

SELECT storage_code, sub_storage, import_no_prefix, import_no_ava, export_no_prefix, export_no_ava, sub_storage_code, input_code, id, org_id, remarks, update_by, create_by, update_date, del_flag, create_date from drug_sub_storage_dept where del_flag = '0'
and org_id = #{orgId}
and storage_code = #{storageCode}
order by sub_storage_code

SELECT 

FROM drug_storage_dept a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE drug_storage_dept SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
