mapper.select:
SELECT 

FROM drug_sub_storage_dept a
WHERE a.id = #{id}

		SELECT 

		FROM drug_sub_storage_dept a

			a.del_flag = #{DEL_FLAG_NORMAL}

                and a.org_id = #{orgId}


                and a.storage_code = #{storageCode}




				ORDER BY ${page.orderBy}


				ORDER BY a.sub_storage_code ASC

mapper.update:
UPDATE drug_sub_storage_dept SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
