mapper.select:
SELECT 

FROM clinic_appoints a

WHERE a.id = #{id}

SELECT a.id AS "id", a.org_id AS "orgId", a.visit_date_appted AS "visitDateAppted", a.clinic_label AS "clinicLabel", a.patient_id AS "patientId", a.visit_time_appted AS "visitTimeAppted", a.appt_made_date AS "apptMadeDate", a.mode_code AS "modeCode", a.card_name AS "cardName", a.card_no AS "cardNo", a.serial_no AS "serialNo", a.name AS "name", a.sex AS "sex", a.age AS "age", a.id_no AS "idNo", a.identity AS "identity", a.charge_type AS "chargeType", a.insurance_no AS "insuranceNo", a.insurance_type AS "insuranceType", a.unit_in_contract AS "unitInContract", a.name_phonetic AS "namePhonetic", a.create_date AS "createDate", a.create_by AS "createBy.id", a.update_date AS "updateDate", a.update_by AS "updateBy.id", a.remarks AS "remarks", a.del_flag AS "delFlag", b.clinic_dept AS "clinicDept", b.doctor AS "doctor", b.doctor_title AS doctorTitle, c.clinic_type_name AS clinicType, b.clinic_position AS clinicPosition FROM clinic_appoints a,clinic_index b,clinic_type_setting c
a.clinic_label=b.clinic_label(+) AND c.id=b.clinic_type AND a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name=#{name}
AND a.card_no=#{cardNo}
AND a.visit_date_appted=to_date(#{visitDateAppted},'yyyy-mm-dd')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM clinic_appoints a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE clinic_appoints SET 
	del_flag = '1'
WHERE id = #{id}
