mapper.select:
SELECT 

FROM clinic_schedule a

WHERE a.id = #{id}

SELECT a.id AS "id", a.org_id AS "orgId", b.clinic_label AS "clinicLabel", a.day_of_week AS "dayOfWeek", a.time_desc AS "timeDesc", a.registration_limits AS "registrationLimits", a.appointment_limits AS "appointmentLimits", a.phone_limits AS "phoneLimits", a.web_limits AS "webLimits", a.create_date AS "createDate", a.create_by AS "createBy.id", a.update_date AS "updateDate", a.update_by AS "updateBy.id", a.remarks AS "remarks", a.del_flag AS "delFlag" FROM clinic_schedule a LEFT JOIN clinic_index b on a.clinic_label=b.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.clinic_label=#{clinicLabel}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM clinic_schedule a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE clinic_schedule SET 
	del_flag = '1'
WHERE id = #{id}
