mapper.select:
SELECT 

FROM org_self_service_list a

WHERE a.id = #{id}

		SELECT 

		FROM org_self_service_list a


			a.del_flag = #{DEL_FLAG_NORMAL}

                and a.org_id = #{orgId}




				ORDER BY ${page.orderBy}


				ORDER BY a.service_name

SELECT 

FROM org_self_service_list a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE org_self_service_list SET 
	del_flag = '1'
WHERE id = #{id}
