mapper.select:
SELECT 

FROM dept_vs_ward a

WHERE a.id = #{id}

SELECT 

FROM dept_vs_ward a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


