mapper.select:
SELECT
a.label
FROM sys_dict AS a
WHERE a.type = #{type}
AND a.value = #{value}
AND a.del_flag='0'

SELECT
a.label
FROM sys_dict AS a
WHERE a.type = #{type}
AND a.value = #{value}
AND a.del_flag='0'

SELECT * FROM sys_dict a WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.type = #{type}
AND a.description LIKE '%'||#{description}||'%' '%'+#{description}+'%' CONCAT('%', #{description}, '%')
ORDER BY a.type, a.sort, a.update_date DESC

mapper.update:
UPDATE sys_dict SET
del_flag = '1'
WHERE id = #{id}
