mapper.select:
SELECT 

FROM org_role_vs_service a

WHERE a.id = #{id}

SELECT 

FROM org_role_vs_service a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM org_role_vs_service a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
